import React from "react";
import WaveSurfer from "wavesurfer.js";


class Song {
  constructor(container, file, name){
    this.state = {
      wavesurfer: WaveSurfer.create({
        container: "#"+container,
        waveColor: 'violet',
        progressColor: 'purple',
        file: file,
      }),
      file: file,
      name: name,
      container: container,
    }
    this.state.wavesurfer.load(this.state.file);
  }
  playPause(){
    this.state.wavesurfer.playPause();
  }
  render(){
    this.state.wavesurfer.load(this.state.file);
    return null;
  }

}

export default Song;
