import React from "react";
import WaveSurfer from "wavesurfer.js";


class Song {
  constructor(ctr, fl, title){

      wavesurfer = WaveSurfer.create({
        container: "#"+container,
        waveColor: 'violet',
        progressColor: 'purple',
        file: file,
      }),
      file= fl,
      title = name,
      container = ctr,

    this.state.wavesurfer.load(this.state.file);
  }
  playPause(){
    this.state.wavesurfer.playPause();
  }
  render(){
    this.state.wavesurfer.load(this.state.file);
    return null;
  }

}

export default Song;
