import React from "react";
import { Amplitude } from "amplitudejs";


function Song(idx) {
  function skipTo(e){
    if( Amplitude.getActiveIndex() == idx ){
      var offset = this.getBoundingClientRect();
      var x = e.pageX - offset.left;

      Amplitude.setSongPlayedPercentage( ( parseFloat( x ) / parseFloat( this.offsetWidth) ) * 100 );
    }
  }

  return (
    <div class = "block" style = {{marginLeft: "20px"}}>

      <div>
        <div id="single-song-player">
          <div class="bottom-container">
          <span class="amplitude-play-pause" amplitude-main-play-pause="true" id="play-pause" data-amplitude-song-index = {idx}>
                play
              </span>
            <progress data-amplitude-song-index = {idx} class="progress amplitude-song-played-progress" id={"song-played-progress-"+idx}></progress>
          </div>
          <div class="time-container">

            <span class="current-time">
              <span data-amplitude-song-index = {idx} class="amplitude-current-minutes" ></span>:<span data-amplitude-song-index = {idx} class="amplitude-current-seconds" amplitude-main-current-seconds="true"></span>
            </span>
            <span class="duration">
              <span data-amplitude-song-index = {idx} class="amplitude-duration-minutes" ></span>:<span data-amplitude-song-index = {idx} class="amplitude-duration-seconds" amplitude-main-duration-seconds="true"></span>
            </span>
            </div>
            <div class="control-container">

            </div>
          <div class="meta-container">
            <span data-amplitude-song-index = {idx}amplitude-song-info="name" amplitude-main-song-info="true" class="song-name"></span>
          </div>
        </div>
      </div>

    </div>



  );

}

export default Song;
