import React from "react";
import {Howl, Howler} from 'howler';
import WaveSurfer from 'wavesurfer.js';


function Song(file, title) {
  var song = new Howl({
    src: [file]
  })

  var wavesurfer;
  return (

    <div>
      <div id = "waveform">

        {wavesurfer.load(file)}
      </div>
        {
            wavesurfer = WaveSurfer.create({
              container: '#waveform'
            })
        }
    </div>
  );

}

export default Song;
