import React from "react";
import WaveSurfer from "wavesurfer.js";


function Song(props) {
  console.log(props.container);
var wavesurfer = WaveSurfer.create({
  container: "#"+props.container,
  waveColor: 'violet',
  progressColor: 'purple'
});

wavesurfer.load(props.file);

}

export default Song;
